}

.progress {
  display: none;
  position: absolute;
  bottom: 0;
  left: 0;
  height: 5px;
  background: linear-gradient(to right, #42df96, #16dfed);
}

.Timer.show .progress {
  display: block;
}

import cc from 'classcat';
import styles from './Timer.module.css';

interface Props {
  duration: number;
  remaining: number;
  show: boolean;
}

export default function Timer({
  duration,
  remaining,
  show,
}: Props) {
  const percentage = 100 - (remaining / duration) * 100;

  return (
    <div className={cc({
      [styles.Timer]: true,
      [styles.show]: show,
    })}>
      <div className={styles.progress} style={{
        width: `${percentage}%`,
      }} />
      Connection will be canceled in {remaining} seconds
    </div>
  )
}

import { Edge, Node } from '@xyflow/react';

export const initialNodes: Node[] = [
  {
    id: '1',
    type: 'input',
    data: { label: 'Node 1' },
    position: { x: 250, y: 5 },
    className: 'light',
  },
  {
    id: '2',
    data: { label: 'Node 2' },
    position: { x: 100, y: 100 },
    className: 'light',
  },
  {
    id: '3',
    data: { label: 'Node 3' },
    position: { x: 400, y: 100 },
    className: 'light',
  },
  {
    id: '4',
    data: { label: 'Node 4' },
    position: { x: 400, y: 200 },
    className: 'light',
  },
];

export const initialEdges: Edge[] = [
  { id: 'e1-2', source: '1', target: '2', animated: true },
  { id: 'e1-3', source: '1', target: '3' },
];

import { useRef, useState } from 'react';

const useCountdown = (callback: () => void) => {
  const interval = useRef<NodeJS.Timer>();
  const [remaining, setRemaining] = useState(0);

  const start = (duration: number) => {
    setRemaining(duration);

    interval.current = setInterval(() => {
      setRemaining((prev) => {
        if (prev === 1) {
          clearInterval(interval.current);
          callback();
        }

        return prev - 1;
      });
    }, 1000);
  };

  const stop = () => {
    clearInterval(interval.current);
    setRemaining(0);
  };

  return {
    start,
    remaining,
    stop,
    counting: remaining > 0,
  };
};

export default useCountdown;

import {
  ReactFlow,
  Background,
  MiniMap,
  addEdge,
  ReactFlowProvider,
  Connection,
  Edge,
  useNodesState,
  useEdgesState,
  OnConnectStart,
  OnConnectEnd,
  useStore,
} from '@xyflow/react';

import useCountdown from './hooks/useCountdown';
import { initialEdges, initialNodes } from './data';
import Timer from './Timer';

const CANCEL_AFTER = 5; // seconds

const CancelConnection = () => {
  const [nodes, _, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const cancelConnection = useStore((state) => state.cancelConnection);

  // Cancels connection after 5 seconds
  const countdown = useCountdown(() => cancelConnection());
  const onConnectStart: OnConnectStart = () => countdown.start(CANCEL_AFTER);
  const onConnectEnd: OnConnectEnd = () => countdown.stop();

  const onConnect = (params: Connection | Edge) => setEdges((eds) => addEdge(params, eds));

  return (
    <>
      <Timer duration={CANCEL_AFTER} show={countdown.counting} remaining={countdown.remaining} />
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnectStart={onConnectStart}
        onConnectEnd={onConnectEnd}
        onConnect={onConnect}
        fitView
        maxZoom={2}
      >
        <Background />
        <MiniMap />
      </ReactFlow>
    </>
  );
};

export default () => (
  <ReactFlowProvider>
    <CancelConnection />
  </ReactFlowProvider>
);

