  {
    id: '2',
    hidden: true,
    data: { label: 'Node 2' },
    position: { x: 100, y: 100 },
  },
  {
    id: '3',
    hidden: true,
    data: { label: 'Node 3' },
    position: { x: 400, y: 100 },
  },
  {
    id: '4',
    hidden: true,
    data: { label: 'Node 4' },
    position: { x: 400, y: 200 },
