      return nds.map((n) => {
        return {
          ...n,
          className: n.className === 'light' ? 'dark' : 'light',
        };
      });
    });
  };

  const toggleChildNodes = () => {
    setNodes((nds) => {
      return nds.map((n) => {
        return {
          ...n,
          hidden: !!n.parentId && !n.hidden,
        };
      });
    });
  };

  return (
