import { useStore } from '$lib/store';

type Portal = 'viewport-back' | 'viewport-front' | 'root' | 'edge-labels';

function tryToMount(node: Element, domNode: Element | null, target: Portal | undefined) {
  if (!target || !domNode) {
    return;
  }

  const targetEl = target === 'root' ? domNode : domNode.querySelector(`.svelte-flow__${target}`);

  if (targetEl) {
    targetEl.appendChild(node);
  }
}

export function portal(node: Element, target: Portal | undefined) {
  const { domNode } = $derived(useStore());

  let destroyEffect: (() => void) | undefined;
  // svelte-ignore state_referenced_locally
  if (domNode) {
    // if the domNode is already mounted, we can directly try to mount the node
    tryToMount(node, domNode, target);
  } else {
    // if the domNode is not mounted yet, we need to wait for it to be ready
    destroyEffect = $effect.root(() => {
      $effect(() => {
        tryToMount(node, domNode, target);
        destroyEffect?.();
      });
    });
  }

  return {
    async update(target: Portal | undefined) {
      tryToMount(node, domNode, target);
    },
    destroy() {
      if (node.parentNode) {
        node.parentNode.removeChild(node);
      }
      destroyEffect?.();
    }
  };
}

export function hideOnSSR(): { value: boolean } {
  let hide = $state(typeof window === 'undefined');
  if (hide) {
    const destroyEffect = $effect.root(() => {
      $effect(() => {
        hide = false;
        destroyEffect?.();
      });
    });
  }

  return {
    get value() {
      return hide;
    }
  };
}

/packages/svelte/src/lib/actions/zoom/in.content:
import {
  PanOnScrollMode,
  XYPanZoom,
  type CoordinateExtent,
  type OnPanZoom,
  type PanZoomInstance,
  type Transform,
  type Viewport
} from '@xyflow/system';

type ZoomParams = {
  viewport: Viewport;
  initialViewport: Viewport;
  minZoom: number;
  maxZoom: number;
  setPanZoomInstance: (panZoomInstance: PanZoomInstance) => void;
  onPanZoomStart?: OnPanZoom;
  onPanZoom?: OnPanZoom;
  onPanZoomEnd?: OnPanZoom;
  onPaneContextMenu?: (event: MouseEvent) => void;
  translateExtent: CoordinateExtent;
  zoomOnScroll: boolean;
  zoomOnPinch: boolean;
  zoomOnDoubleClick: boolean;
  panOnScroll: boolean;
  panOnDrag: boolean | number[];
  panOnScrollSpeed: number;
  panOnScrollMode: PanOnScrollMode;
  zoomActivationKeyPressed: boolean;
  preventScrolling: boolean;
  // last two instances of 'classname' being used
  // changing it to class would require object restructuring for use with panZoomInstance.update
  noPanClassName: string;
  noWheelClassName: string;
  userSelectionActive: boolean;
  lib: string;
  paneClickDistance: number;
  selectionOnDrag?: boolean;
  onTransformChange: (transform: Transform) => void;
  onDraggingChange: (dragging: boolean) => void;
  connectionInProgress: boolean;
};

export default function zoom(domNode: Element, params: ZoomParams) {
  const {
    minZoom,
    maxZoom,
    initialViewport,
    onPanZoomStart,
    onPanZoom,
    onPanZoomEnd,
    translateExtent,
    setPanZoomInstance,
    onDraggingChange,
    onTransformChange
  } = params;

  const panZoomInstance = XYPanZoom({
    domNode,
    minZoom,
    maxZoom,
    translateExtent,
    viewport: initialViewport,
    onPanZoom,
    onPanZoomStart,
    onPanZoomEnd,
    onDraggingChange
  });

  const viewport = panZoomInstance.getViewport();
  if (
    initialViewport.x !== viewport.x ||
    initialViewport.y !== viewport.y ||
    initialViewport.zoom !== viewport.zoom
  ) {
    onTransformChange([viewport.x, viewport.y, viewport.zoom]);
  }

  setPanZoomInstance(panZoomInstance);

  panZoomInstance.update(params);

  return {
    update(params: ZoomParams) {
      panZoomInstance.update(params);
    }
  };
}

