/tests/playwright/e2e/props.spec.ts.content:
import { test, expect } from '@playwright/test';

import { FRAMEWORK } from './constants';

test.describe('Props', () => {
  test.describe('colorMode', async () => {
    test('render default light color mode', async ({ page }) => {
      await page.goto('/examples/color-mode');

      await expect(page.locator(`.${FRAMEWORK}-flow__node`).first()).toHaveCSS('visibility', 'visible');

      await expect(page.locator(`.${FRAMEWORK}-flow`)).not.toHaveClass(/dark/);
    });

    test('render dark color mode', async ({ page }) => {
      await page.goto('/examples/color-mode');
      await expect(page.locator(`.${FRAMEWORK}-flow__node`).first()).toHaveCSS('visibility', 'visible');

      await page.getByTestId('colormode-select').selectOption({ label: 'dark' });

      await expect(page.locator(`.${FRAMEWORK}-flow`)).toHaveClass(/dark/);
    });
  });
});

/tests/playwright/e2e/utils.ts.content:
const MATCH_ALL_NUMBERS = /[\d\.]+/g;

// Type "Locator" not exported...
export async function getTransform(element) {
  const transformString = await element.evaluate((el) => {
    return el.style.transform;
  });

  // Parses all numbers in f.ex "translate(590px, 324px) scale(2)""
  const transforms = transformString.match(MATCH_ALL_NUMBERS);
  return {
    translateX: parseFloat(transforms![0]),
    translateY: parseFloat(transforms![1]),
    scale: parseFloat(transforms![2]),
  };
}

